import React, {useState, useEffect} from 'react'
import { useGlobalContext} from "../../context/context";
import { ResponsiveBar } from '@nivo/bar';


const Trades = () => {
    const { gameData } = useGlobalContext();

    const data = [
        {
            "playerName": "Anja",
            "price": 130,
            "maxTxFee": 43
        },
        {
            "playerName": "Nejc",
            "price": 187,
            "maxTxFee": 43
        },
        {
            "playerName": "Marko",
            "price": 194,
            "maxTxFee": 43
        }
    ];

    useEffect(() => {

    }, [gameData]);

    return (
        <>
            <div className="chart-container">
                <ResponsiveBar
                    data={data}
                    keys={[ 'price', 'maxTxFee' ]}
                    indexBy="playerName"
                    margin={{ top: 50, right: 130, bottom: 50, left: 60 }}
                    padding={0.3}
                    valueScale={{ type: 'linear' }}
                    colors={{ scheme: 'paired' }}

                    axisTop={null}
                    axisRight={null}
                    axisBottom={{
                        tickSize: 5,
                        tickPadding: 5,
                        tickRotation: 0,
                        legend: 'player',
                        legendPosition: 'middle',
                        legendOffset: 40,

                    }}
                    axisLeft={{
                        tickSize: 5,
                        tickPadding: 5,
                        tickRotation: 0,
                        legend: 'price',
                        legendPosition: 'middle',
                        legendOffset: -40
                    }}
                    labelSkipWidth={12}
                    labelSkipHeight={12}
                    labelTextColor={{ from: 'color', modifiers: [ [ 'darker', 1.6 ] ] }}
                    legends={[
                        {
                            dataFrom: 'keys',
                            anchor: 'bottom-right',
                            direction: 'column',
                            justify: false,
                            translateX: 120,
                            translateY: 0,
                            itemsSpacing: 2,
                            itemWidth: 100,
                            itemHeight: 20,
                            itemDirection: 'left-to-right',
                            itemOpacity: 0.85,
                            symbolSize: 20,
                            effects: [
                                {
                                    on: 'hover',
                                    style: {
                                        itemOpacity: 1
                                    }
                                }
                            ]
                        }
                    ]}
                    animate={true}
                    motionStiffness={90}
                    motionDamping={15}
                />
            </div>
        </>
    )
};

export default Trades
