import React, {useState, useEffect} from 'react';
import ReactTooltip from 'react-tooltip';
import { useGlobalContext} from "../../context/context";
import PieChart from '../misc/PieChart';


const Trades = () => {
    const { gameData } = useGlobalContext();
    const [chartDataArray, setChartDataArray] = useState([]);

    useEffect(() => {
        const createDataArray = async () => {
            const dataArray = await gameData.players.map((item) => {
                return({
                    id: item.playerName,
                    value: parseInt(item.stake)
                });
            });
            setChartDataArray(dataArray);
        };
        createDataArray();
    }, [gameData]);

    useEffect(() => {

    }, [gameData]);

    return (
        <>
            <div className="blockchain-container">
                <div className="piechart-container">
                    <div className="piechart-container-text">
                        <h3>Blockchain - stake</h3>
                    </div>
                    <div className="piechart-container-chart">
                        <PieChart data={chartDataArray}/>
                    </div>
                </div>
                <div className="piechart-container">
                    <div className="table-pending-transactions-container-text">
                        <h4>Pending transactions</h4>
                    </div>
                    <div className="table-pending-transactions-container">
                        <table className="table-pending-transactions">
                            <thead>
                            <tr>
                                <th className="table-pending-transactions-head">Nr.</th>
                                <th className="table-pending-transactions-head">Transaction</th>
                                <th className="table-pending-transactions-head">Tx Fee</th>
                            </tr>
                            </thead>
                            <tbody>
                            <tr data-tip data-for='happyFace'>
                                <td><strong>1</strong></td>
                                <td>Nejc &#8646; Marko</td>
                                <td>15</td>
                            </tr>
                            <ReactTooltip id='happyFace' type='error'>
                                <span>Show happy face</span>
                            </ReactTooltip>
                            <tr>
                                <td><strong>2</strong></td>
                                <td>Nejc & Marko</td>
                                <td>18</td>
                            </tr>
                            <tr>
                                <td><strong>3</strong></td>
                                <td>Nejc & Marko</td>
                                <td>13</td>
                            </tr>
                            <tr>
                                <td><strong>4</strong></td>
                                <td>Nejc & Marko</td>
                                <td>14</td>
                            </tr>
                            <tr>
                                <td><strong>5</strong></td>
                                <td>Nejc & Marko</td>
                                <td>12</td>
                            </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </>
    )
};

export default Trades
