import React from 'react'
import { useGlobalContext} from "../../context/context";
import { FaTimes } from 'react-icons/fa';
import Axios from "axios/index";

const CancelTransactionModal = () => {
    const { isModalCancelTransactionOpen, closeModalCancelTransaction, modalCancelTransactionContent } = useGlobalContext();

    const confirm = async () => {
        try {
            const token = localStorage.getItem("auth-token");
            const playerId = localStorage.getItem("playerId");
            console.log(token);
            const loginRes =  await Axios.delete(`http://localhost:8000/player/cancelTransaction/${playerId}`, {transactionId: modalCancelTransactionContent.id}, {
                headers: {"authorization": "Bearer " + token}
            });
            closeModalCancelTransaction();
        } catch(err) {
            console.log(err);
        }
    };

    return (
        <div
            className={`${
                isModalCancelTransactionOpen ? 'modal-confirm-overlay show-modal-confirm' : 'modal-confirm-overlay'
                }`}
        >
            <div className='modal-confirm-container'>
                <h3>Are you sure you want to cancel transaction:</h3>
                <ul>
                    <li>Consumer: {modalCancelTransactionContent.consumer}</li>
                    <li>Provider: {modalCancelTransactionContent.provider}</li>
                    <li>typeOfService: {modalCancelTransactionContent.typeOfService}</li>
                    <li>price: {modalCancelTransactionContent.price}$</li>
                    <li>txFee: {modalCancelTransactionContent.txFee}$</li>
                </ul>
                <button className='close-modal-btn' onClick={closeModalCancelTransaction}>
                    <FaTimes></FaTimes>
                </button>
                <button className='confirm-modal-btn' onClick={confirm}>Confirm</button>
            </div>
        </div>
    )
};

export default CancelTransactionModal
