import React, { useState, useEffect} from 'react';
import {useGlobalContext} from "../../context/context";
import Axios from 'axios';

import Sidebar from '../layout/Sidebar';
import Modal from '../misc/Modal';
import Stats from '../misc/Stats'

import {FaBars} from 'react-icons/fa';

import stakeImg from '../../assets/stake.png';


const Home = () => {
    const {openSidebar, playerData, setPlayerData, setModalContent} = useGlobalContext();
    const [isGameOn, setIsGameOn] = useState(false);

    const getGameData = async () => {
        try {
            let token = localStorage.getItem("auth-token");
            let playerId = localStorage.getItem("playerId");
            const gameRes = await Axios.get(`http://localhost:8000/player/isGameOn/${playerId}`, {
                headers: {"authorization": "Bearer " + token}
            });
            setIsGameOn(gameRes.data.game);
        } catch (err) {
            console.log(err)
        }
    };

    const getPlayerData = async () => {
        try {
            let token = localStorage.getItem("auth-token");
            let playerId = localStorage.getItem("playerId");
            const playerRes = await Axios.get(`http://localhost:8000/player/${playerId}`, {
                headers: {"authorization": "Bearer " + token}
            });
            setPlayerData({
                token,
                player: playerRes.data.player
            });
        } catch (err) {
            console.log(err)
        }
    };

    useEffect(() => {
        const checkLoggedInAndGame = async () => {
            let token = localStorage.getItem("auth-token");
            let playerId = localStorage.getItem("playerId");
            if (token !== (null || "") && playerId !== (null || "")) {
                const interval = setInterval(() => {
                    getPlayerData();
                    getGameData();
                }, 1000);
                if (!isGameOn) {
                    setModalContent('Wait for the admin to start the game!');
                }
                return () => {
                    clearInterval(interval);
                };
            } else {
                setModalContent('Please log in!');
            }
        };
        checkLoggedInAndGame();


    }, []);

    return (
        <div>
            <div>
                {
                    playerData.player ? (
                        <div>
                            {
                                isGameOn ? (
                                    <div className="home-grid">
                                        <div className="item-sidebar">
                                            <main>
                                                <button className="sidebar-toggle" onClick={openSidebar}>
                                                <FaBars/>
                                                </button>
                                            </main>
                                        </div>
                                        <div className="item-stats">

                                            <Stats/>
                                        </div>
                                        <div className="item-trade">
                                            <h2>These are trades</h2>
                                            <img src={stakeImg} alt={"stake"}/>
                                        </div>
                                        <div className="item-blockchain">
                                            <h2>This is blockchain</h2>
                                        </div>
                                    </div>
                                ) : (
                                    <>
                                        <Modal/>
                                    </>
                                )
                            }
                        </div>

                    ) : (
                        <>
                            <Modal/>
                        </>
                    )
                }
            </div>
            <Sidebar/>
        </div>
    )
};

export default Home
