import React from 'react'
import { useGlobalContext} from "../../context/context";

const Sidebar = () => {
    const { gameData, openStakeModal, openSetPriceModal } = useGlobalContext();

    return (
        <div className="sidebar">
            <button className="sidebar-btn" onClick={openSetPriceModal}>Set Price</button>
            <button className="sidebar-btn" onClick={openStakeModal}>Stake/Unstake</button>
        </div>
    )
};

export default Sidebar
