import React, {useState, useEffect} from 'react'
import { useGlobalContext} from "../../context/context";
import BarChart from '../misc/BarChart';


const Trades = () => {
    const { gameData } = useGlobalContext();
    const [dataArray1, setDataArray1] = useState([]);
    const [dataArray2, setDataArray2] = useState([]);

    useEffect(() => {
        const sortDataArrays = async () => {
            const array1 = await gameData.otherPrices.filter(item => item.typeOfService === gameData.player.typeOfOtherService1);
            const array2 = await gameData.otherPrices.filter(item => item.typeOfService === gameData.player.typeOfOtherService2);
            await array1.forEach((item) => {
                item.price = parseInt(item.price);
                item.maxTxFee = 10
            });
            await array2.forEach((item) => {
                item.price = parseInt(item.price);
                item.maxTxFee = 10
            });
            await array1.sort((a, b) => parseInt(a.price) - parseInt(b.price));
            await array2.sort((a, b) => parseInt(a.price) - parseInt(b.price));
            setDataArray1(array1);
            setDataArray2(array2);
        };
        sortDataArrays();
    }, [gameData]);

    const data = [
        {
            "playerName": "Anja",
            "price": 130,
            "maxTxFee": 43
        },
        {
            "playerName": "Nejc",
            "price": 177,
            "maxTxFee": 43
        },
        {
            "playerName": "Marko",
            "price": 179,
            "maxTxFee": 43
        },
        {
            "playerName": "Luka",
            "price": 130,
            "maxTxFee": 43
        },
        {
            "playerName": "ASd",
            "price": 177,
            "maxTxFee": 43
        },
        {
            "playerName": "ASD",
            "price": 179,
            "maxTxFee": 43
        },
        {
            "playerName": "ert",
            "price": 179,
            "maxTxFee": 43
        },
        {
            "playerName": "ASDsdf",
            "price": 179,
            "maxTxFee": 43
        },
        {
            "playerName": "xcvb",
            "price": 179,
            "maxTxFee": 43
        },
        {
            "playerName": "ASnbvmD",
            "price": 179,
            "maxTxFee": 43
        }
    ];

    return (
        <>
            <div className="trades-container">
                <div className="chart-container">
                    <div className="chart-container-text">
                        <h3>Service {`${gameData.player.typeOfOtherService1}`}</h3>
                    </div>
                    <div className="chart-container-chart">
                        <BarChart data={dataArray1} scheme={'paired'}/>
                    </div>
                </div>
                <div className="chart-container">
                    <div className="chart-container-text">
                        <h3>Service {`${gameData.player.typeOfOtherService2}`}</h3>
                    </div>
                    <div className="chart-container-chart">
                        <BarChart data={dataArray2} scheme={'pink_yellowGreen'}/>
                    </div>
                </div>
            </div>
        </>
    )
};

export default Trades
