import React, { useState, useContext } from 'react';
//import { Alert } from 'react-bootstrap';
import {useGlobalContext} from "../../context/context";
import Axios from 'axios';
import { useHistory } from 'react-router-dom';
import loginImg from '../../assets/login.svg';

export default function Login () {
    const [playerName, setPlayerName] = useState();
    const [password, setPassword] = useState();

    const [showAlert, setShowAlert] = useState(false);

    const { setPlayerData } = useGlobalContext();
    const history = useHistory();

    const submit = async (e) => {
        try {
            e.preventDefault();
            const loginPlayer = { playerName, password };
            const loginRes =  await Axios.post("http://localhost:8000/player/login", loginPlayer);
            const playerRes = await Axios.get(`http://localhost:8000/player/${loginRes.data.id}`, {
                headers: {"authorization": "Bearer " + loginRes.data.token}
            });
            setPlayerData({
                token: loginRes.data.token,
                player: playerRes.data.player
            });
            localStorage.setItem("auth-token", loginRes.data.token);
            localStorage.setItem("playerId", loginRes.data.id);
            history.push("/");
        } catch(err) {
            if (err.response.data.message === "Auth failed") {
                setShowAlert(true);
            }
        }

    };

    return (
        <div className={"base-container"}>
            <div className={"header"}>Login</div>
            <div className={"content"}>
                <div className={"image"}>
                    <img src={loginImg}/>
                </div>
                <div className={"form"}>
                    <div className={"form-group"}>
                        <label htmlFor={"playerName"}>Player name</label>
                        <input type={"text"} name={"playerName"} placeholder={"Enter name"} onChange={e => setPlayerName(e.target.value)}/>
                    </div>
                    <div className={"form-group"}>
                        <label htmlFor={"password"}>Password</label>
                        <input type={"password"} name={"password"} placeholder={"Enter password"} onChange={e => setPassword(e.target.value)}/>
                    </div>
                </div>
            </div>
            <div>
                The name or password is incorrect!
            </div>
            <div className="footer">
                <button type="button" className="btn" onClick={submit}>
                    Login
                </button>
            </div>
        </div>
    )
};
