import React, {useState, useEffect} from 'react';
import { useGlobalContext} from "../../context/context";
import { FaTimes } from 'react-icons/fa';
import Axios from "axios/index";

const TradeModal = () => {
    const { gameData, isTradeModalOpen, closeTradeModal, tradeModalContent } = useGlobalContext();
    const [txFee, setTxFee] = useState();
    const [showAlert, setShowAlert] = useState(false);
    const [alertContent, setAlertContent] = useState('');

    const confirm = async () => {
        try {
            if (isNaN(txFee)) {
                setAlertContent('Input must be a number');
                setShowAlert(true);
            } else {
                setAlertContent('');
                setShowAlert(false);
            }
        } catch(err) {
            console.log(err);
        }
    };

    useEffect(() => {

    }, [gameData]);

    return (
        <div
            className={
                `${isTradeModalOpen ? 'modal-confirm-overlay show-modal-confirm' : 'modal-confirm-overlay'}`
            }
        >
            <div className='modal-confirm-container'>
                <h3>Create Trade</h3>
                <ul>
                    <li>Provider: {tradeModalContent.playerName}</li>
                    <li>typeOfService: {tradeModalContent.typeOfService}</li>
                    <li>price: {tradeModalContent.price}$</li>
                </ul>
                <div className={"modal-input-group"}>
                    <label htmlFor={"txFee"}>Tx Fee</label>
                    <div className="modal-input-group-container">
                        <input type={"text"} name={"txFee"} placeholder={"Enter tx fee"} onChange={e => setTxFee(e.target.value)}/>
                        <span>$</span>
                    </div>
                    <span>Current Max Tx Fee: {tradeModalContent.maxTxFee}$</span>
                </div>
                <div className={`${showAlert? 'modal-input-alert show-modal-input-alert' : 'modal-input-alert'}`}>
                    {alertContent}
                </div>
                <button className='close-modal-btn' onClick={() => {
                    closeTradeModal();
                }}>
                    <FaTimes></FaTimes>
                </button>
                <button className='confirm-modal-btn' onClick={confirm}>Confirm</button>
            </div>
        </div>
    )
};

export default TradeModal
