import React, {useState, useEffect} from 'react';
import ReactTooltip from 'react-tooltip';
import { useGlobalContext} from "../../context/context";
import PieChart from '../misc/PieChart';


const Trades = () => {
    const { gameData } = useGlobalContext();
    const [chartDataArray, setChartDataArray] = useState([]);

    const renderTableData = async () => {
        const transactions = await gameData.allPendingTransactions.sort((a, b) => parseInt(a.txFee) - parseInt(b.txFee));
        return transactions.map(async (item, index) => {
            const { consumer, provider, typeOfService, amountOfService, price, txFee } = item;
            const consumerObject = await gameData.players.filter(item => item._id === consumer);
            const providerObject = await gameData.players.filter(item => item._id === provider);
            return (
                <tr data-tip data-for={item._id}>
                    <td><strong>{index + 1}</strong></td>
                    <td>{consumerObject.playerName} &#8646; {providerObject.playerName}</td>
                    <td>{txFee}</td>
                    <ReactTooltip id={item._id} place="bottom" type="dark" effect="solid">
                        <ul>
                            <li>Consumer: {consumerObject.playerName}</li>
                            <li>Provider: {providerObject.playerName}</li>
                            <li>typeOfService: {typeOfService}</li>
                            <li>amountOfService: {amountOfService}</li>
                            <li>price: {price}$</li>
                        </ul>
                    </ReactTooltip>
                </tr>
            )
        })
    };

    useEffect(() => {
        const createDataArray = async () => {
            const dataArray = await gameData.players.map((item) => {
                return({
                    id: item.playerName,
                    value: parseInt(item.stake)
                });
            });
            setChartDataArray(dataArray);
        };
        createDataArray();
    }, [gameData]);

    useEffect(() => {

    }, [gameData]);

    return (
        <>
            <div className="blockchain-container">
                <div className="piechart-container">
                    <div className="piechart-container-text">
                        <h3>Blockchain - stake</h3>
                    </div>
                    <div className="piechart-container-chart">
                        <PieChart data={chartDataArray}/>
                    </div>
                </div>
                <div className="piechart-container">
                    <div className="table-pending-transactions-container-text">
                        <h4>Pending transactions</h4>
                    </div>
                    <div className="table-pending-transactions-container">
                        <table className="table-pending-transactions">
                            <thead>
                            <tr>
                                <th className="table-pending-transactions-head">Nr.</th>
                                <th className="table-pending-transactions-head">Transaction</th>
                                <th className="table-pending-transactions-head">Tx Fee</th>
                            </tr>
                            </thead>
                            <tbody>
                                {renderTableData}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </>
    )
};

export default Trades
