import React, { useState, useEffect} from 'react';
import {useGlobalContext} from "../../context/context";
import Axios from 'axios';

import Sidebar from '../layout/Sidebar';
import Modal from '../misc/Modal';
import Stats from '../misc/Stats';
import Trades from '../misc/Trades';
import Blockchain from '../misc/Blockchain';
import ConfirmModal from '../misc/ConfirmModal';
import StakeModal from '../misc/StakeModal';
import TradeModal from '../misc/TradeModal';

import {FaBars} from 'react-icons/fa';


const Home = () => {
    const {openSidebar, gameData, setGameData, setModalContent} = useGlobalContext();
    const [isGameOn, setIsGameOn] = useState(false);

    const getGameData = async () => {
        try {
            let token = localStorage.getItem("auth-token");
            let playerId = localStorage.getItem("playerId");
            const gameRes = await Axios.get(`http://localhost:8000/player/gameData/${playerId}`, {
                headers: {"authorization": "Bearer " + token}
            });
            setGameData({
                token,
                player: gameRes.data.player,
                otherPrices: gameRes.data.otherPrices,
                pendingTrades: gameRes.data.pendingTrades,
                requestedTrades: gameRes.data.requestedTrades,
                allPendingTransactions: gameRes.data.allPendingTransactions,
                totalStake: gameRes.data.totalStake,
                players: gameRes.data.players
            });
            setIsGameOn(gameRes.data.isGameOn);
        } catch (err) {
            if (err.response !== undefined && err.response.data.message === "Auth failed") {
                localStorage.setItem("auth-token", "");
                localStorage.setItem("playerId", "");
                window.location.reload(false);
            }
        }
    };

    useEffect(() => {
        const checkLoggedInAndGame = async () => {
            let token = localStorage.getItem("auth-token");
            let playerId = localStorage.getItem("playerId");
            if (token !== (null || "") && playerId !== (null || "")) {
                const interval = setInterval(() => {
                    getGameData();
                }, 100);
                if (!isGameOn) {
                    setModalContent('Wait for the admin to start the game!');
                }
                return () => {
                    clearInterval(interval);
                };
            } else {
                setModalContent('Please log in!');
            }
        };
        checkLoggedInAndGame();
    }, []);

    return (
        <div>
            <div>
                {
                    gameData.player ? (
                        <div>
                            {
                                isGameOn ? (
                                    <div>
                                        <div className="home-grid">
                                            <div className="item-sidebar">
                                                <Sidebar/>
                                            </div>
                                            <div className="item-stats">
                                                <Stats/>
                                            </div>
                                            <div className="item-trade">
                                                <Trades/>
                                            </div>
                                            <div className="item-blockchain">
                                                <Blockchain/>
                                            </div>
                                        </div>
                                        <ConfirmModal/>
                                        <StakeModal/>
                                        <TradeModal/>
                                    </div>
                                ) : (
                                    <>
                                        <Modal/>
                                    </>
                                )
                            }
                        </div>

                    ) : (
                        <>
                            <Modal/>
                        </>
                    )
                }
            </div>
        </div>
    )
};

export default Home
