import React from 'react'
import { ResponsiveBar } from '@nivo/bar';

const BarChart = ({data, scheme}) => (
    <ResponsiveBar
        data={data}
        keys={[ 'price', 'maxTxFee' ]}
        indexBy="playerName"
        margin={{ top: 10, right: 130, bottom: 30, left: 70 }}
        padding={0.25}
        valueScale={{ type: 'linear' }}
        theme={{ "fontSize": 14, fontFamily: "Roboto, sans-serif", axis: { legend: { text: { fontSize: "16px", fontWeight: "bold", fontFamily: "Roboto, sans-serif" } } } }}
        colors={{ scheme: scheme}}
        borderColor={{ from: 'color', modifiers: [ [ 'darker', 1.6 ] ] }}
        axisTop={null}
        axisRight={null}
        axisBottom={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
        }}
        axisLeft={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: 'PRICE [$]',
            legendPosition: 'middle',
            legendOffset: -50,
            tickValues: 5
        }}
        labelSkipWidth={12}
        labelSkipHeight={12}
        labelTextColor="white"
        legends={[
            {
                dataFrom: 'keys',
                anchor: 'bottom-right',
                direction: 'column',
                justify: false,
                translateX: 120,
                translateY: 0,
                itemsSpacing: 2,
                itemWidth: 100,
                itemHeight: 20,
                itemDirection: 'left-to-right',
                itemOpacity: 0.85,
                symbolSize: 20,
                effects: [
                    {
                        on: 'hover',
                        style: {
                            itemOpacity: 1
                        }
                    }
                ]
            }
        ]}
        animate={true}
        motionStiffness={90}
        motionDamping={15}
    />
);


export default BarChart
