import React from 'react';
import { Link } from 'react-router-dom';
import { links } from '../../links';
import { FaMoneyBillAlt, FaBusinessTime, FaChartPie } from 'react-icons/fa';
import { useGlobalContext} from "../../context/context";

const Sidebar = () => {
    const { gameData } = useGlobalContext();

    return (
        <div className="sidebar">
            <ul className='sidebar-links'>
                {links.map((link) => {
                    const { id, url, text, icon } = link;
                    return (
                        <li key={id}>
                            <Link to={url}>
                                {icon}
                                {text}
                            </Link>
                        </li>
                    );
                })}
            </ul>
            <div className="stats-sidebar">
                <h3>Stats</h3>
                <div className="stats-sidebar-values">
                    <div>
                        <FaMoneyBillAlt style={{color: "green"}}/>
                    </div>
                    <div>
                        <h4>{gameData.player.balance}</h4>
                    </div>
                    <div>
                        <FaBusinessTime style={{color: "#31b1ff"}}/>
                    </div>


                    <h4>{gameData.player.amountOfAvailableService === 1 ? 'AVAILABLE' : 'UNAVAILABLE'}</h4>
                    <FaChartPie style={{color: "#ffbf90"}}/>
                    <h4>{gameData.player.stake}</h4>
                </div>

            </div>
        </div>
    )
};

export default Sidebar
