import React, {useState, useEffect} from 'react'
import { useGlobalContext} from "../../context/context";
import PieChart from '../misc/PieChart';


const Trades = () => {
    const { gameData } = useGlobalContext();
    const [chartDataArray, setChartDataArray] = useState([]);

    useEffect(() => {
        const createDataArray = async () => {
            const dataArray = await gameData.players.map((item) => {
                return({
                    id: item.playerName,
                    value: parseInt(item.stake)
                });
            });
            setChartDataArray(dataArray);
        };
        createDataArray();
    }, [gameData]);

    useEffect(() => {

    }, [gameData]);

    return (
        <>
            <div className="blockchain-container">
                <div className="piechart-container">
                    <div className="piechart-container-text">
                        <h3>Blockchain - stake</h3>
                    </div>
                    <div className="piechart-container-chart">
                        <PieChart data={chartDataArray}/>
                    </div>
                </div>
                <div className="piechart-container">
                    <div className="table-container-text">
                        <h4>Pending transactions</h4>
                        <table>
                            <thead>
                            <tr>
                                <th>Nr.</th>
                                <th>Transaction</th>
                                <th>Tx Fee</th>
                            </tr>
                            </thead>
                            <tbody>
                            <tr>
                                <td><strong>showSpeed</strong></td>
                                <td>15</td>
                                <td>The speed of the show/reveal</td>
                            </tr>
                            <tr>
                                <td><strong>showEasing</strong></td>
                                <td>'linear'</td>
                                <td>The easing of the show/reveal</td>
                            </tr>
                            <tr>
                                <td><strong>hideSpeed</strong></td>
                                <td>50</td>
                                <td>The speed of the hide/conceal</td>
                            </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </>
    )
};

export default Trades
