import React, { useEffect, useState } from 'react'
import { ResponsiveBar } from '@nivo/bar';
import { useGlobalContext} from "../../context/context";

const BarChart = ({data}) => {
    const { gameData, openTradeModal, setTradeModalContent, openCancelOrderModal, setCancelOrderModalContent } = useGlobalContext();
    const [scale, setScale] = useState('linear');

    const setTradeModal = (data) => {
        if (data.typeOfService !== gameData.player.typeOfService) {
            setTradeModalContent(data);
            openTradeModal();
        } else {
            if (data.playerName === gameData.player.playerName) {
                setCancelOrderModalContent(data);
                openCancelOrderModal();
            }
        }
    };

    const mouseHover = (data, event) => {
        if (data.typeOfService !== gameData.player.typeOfService) {
            event.target.style.cursor = 'pointer';
        } else {
            if (data.playerName === gameData.player.playerName) {
                event.target.style.cursor = 'pointer';
            }
        }
    };


    useEffect(() => {
        const setChartScale = async () => {
            const prices = await gameData.orders.filter(order => order.price > 100)
            if (!Array.isArray(prices) || !prices.length) {
                setScale('linear')
            } else {
                setScale('symlog')
            }
        };
        setChartScale()
    }, [gameData]);

    return (
        <ResponsiveBar
            data={data}
            onClick={(data) => setTradeModal(data.data)}
            onMouseEnter={(data, event) => mouseHover(data.data, event)}
            keys={[ 'price' ]}
            indexBy="playerName"
            margin={{ top: 10, right: 130, bottom: 50, left: 70 }}
            padding={0.25}
            valueScale={{ type: scale }}
            theme={{ "fontSize": 14, fontFamily: "Roboto, sans-serif", axis: { legend: { text: { fontSize: "16px", fontWeight: "bold", fontFamily: "Roboto, sans-serif" } } } }}
            colors={d => d.data.color}
            borderColor={{ from: 'color', modifiers: [ [ 'darker', 1.6 ] ] }}
            axisTop={null}
            axisRight={null}
            axisBottom={{
                tickSize: 5,
                tickPadding: 5,
                tickRotation: 0,
            }}
            axisLeft={{
                tickSize: 5,
                tickPadding: 5,
                tickRotation: 0,
                legend: 'PRICE',
                legendPosition: 'middle',
                legendOffset: -50,
                tickValues: 5
            }}
            labelSkipWidth={12}
            labelSkipHeight={12}
            labelTextColor="white"
            legends={[
                {
                    dataFrom: 'keys',
                    anchor: 'bottom-right',
                    direction: 'column',
                    justify: false,
                    translateX: 120,
                    translateY: 0,
                    itemsSpacing: 2,
                    itemWidth: 100,
                    itemHeight: 20,
                    itemDirection: 'left-to-right',
                    itemOpacity: 0.85,
                    symbolSize: 20,
                    effects: [
                        {
                            on: 'hover',
                            style: {
                                itemOpacity: 1
                            }
                        }
                    ]
                }
            ]}
            animate={true}
            motionStiffness={90}
            motionDamping={15}
        />
    )
};


export default BarChart
